Scripts.OnVerticalScroll:
		local scrollbar = getglobal(self:GetName().."ScrollBar");
        scrollbar:SetValue(offset);
        local min;
        local max;
        min, max = scrollbar:GetMinMaxValues();
        if ( offset == 0 ) then
          getglobal(scrollbar:GetName().."ScrollUpButton"):Disable();
        else
          getglobal(scrollbar:GetName().."ScrollUpButton"):Enable();
        end
        if ((scrollbar:GetValue() - max) == 0) then
          getglobal(scrollbar:GetName().."ScrollDownButton"):Disable();
        else
          getglobal(scrollbar:GetName().."ScrollDownButton"):Enable();
        end
		GuildPanelInformationScrollBar_OnVerticalScroll()

Scripts.OnTextChanged:
local scrollBar = getglobal(this:GetParent():GetName().."ScrollBar")
local min;
local max;
min, max = scrollBar:GetMinMaxValues();
if ( max > 0 and (this.max ~= max) ) then
	this.max = max;
	scrollBar:SetValue(max);
end

Scripts.OnMouseUp:
if button == "LeftButton" then
	GuildPanel:StartImporter()
elseif button == "RightButton" then

	local x,y = GetCursorPosition()
	x = x/UIParent:GetEffectiveScale();
	y = y/UIParent:GetEffectiveScale();
	ppmItems = {
		{
			name = "This Wizard scans the guild public notes for alt information.",
			disabled = true
		},
		{
			name = "-Okay-",
			func = function ()
				GuildPanel:ClosePopupMenu("ButtonTooltip")
			end
		}
	}
	GuildPanel:DrawPopupMenu("ButtonTooltip", x+20, y-(#ppmItems*16), UIParent, "BOTTOMLEFT", 300, ppmItems)
end

if button == "LeftButton" then
	GuildPanel:StartDownloader()
elseif button == "RightButton" then

	local x,y = GetCursorPosition()
	x = x/UIParent:GetEffectiveScale();
	y = y/UIParent:GetEffectiveScale();
	ppmItems = {
		{
			name = "This Wizard will download the Alts database from another guild member.",
			disabled = true
		},
		{
			name = "-Okay-",
			func = function ()
				GuildPanel:ClosePopupMenu("ButtonTooltip")
			end
		}
	}
	GuildPanel:DrawPopupMenu("ButtonTooltip", x+20, y-(#ppmItems*16), UIParent, "BOTTOMLEFT", 365, ppmItems)
end

if button == "LeftButton" then
	table.wipe(GuildPanel.db.char.alts)
	table.wipe(GuildPanel.db.char.mains)
	GuildPanel.db.char.dbTimestamp = 0
	GuildPanel:DrawDataView()
elseif button == "RightButton" then

	local x,y = GetCursorPosition()
	x = x/UIParent:GetEffectiveScale();
	y = y/UIParent:GetEffectiveScale();
	ppmItems = {
		{
			name = "This button will erase all Alt/Main associations in the GuildPanel database.",
			disabled = true
		},
		{
			name = "There will be no confirmation.",
			disabled = true
		},
		{
			name = "-Okay-",
			func = function ()
				GuildPanel:ClosePopupMenu("ButtonTooltip")
			end
		}
	}
	GuildPanel:DrawPopupMenu("ButtonTooltip", x+20, y-(#ppmItems*16), UIParent, "BOTTOMLEFT", 370, ppmItems)
end

local ppmItems = {
	{
		name = "With Selected",
		func = function () 
			GuildPanel:SetActionGroup("SELECTED")
			GuildPanel:ClosePopupMenu("ActionGroupSelect")
		end
	},
	{
		name = "With Marked",
		func = function () 
			GuildPanel:SetActionGroup("MARKED")
			GuildPanel:ClosePopupMenu("ActionGroupSelect")
		end
	}
}
GuildPanel:DrawPopupMenu("ActionGroupSelect", -10, -1, self, "TOPRIGHT",80, ppmItems)

